import { TimeStruct } from "./basics";
import { TimeZone } from "./timezone";
/**
 * TimeStruct plus zone
 */
export interface AwareTimeStruct {
    /**
     * The time struct
     */
    time: TimeStruct;
    /**
     * The time zone
     */
    zone?: TimeZone;
}
/**
 * Checks if a given datetime string is according to the given format
 * @param dateTimeString The string to test
 * @param formatString LDML format string
 * @param allowTrailing Allow trailing string after the date+time
 * @returns true iff the string is valid
 */
export declare function parseable(dateTimeString: string, formatString: string, allowTrailing?: boolean): boolean;
/**
 * Parse the supplied dateTime assuming the given format.
 *
 * @param dateTimeString The string to parse
 * @param formatString The formatting string to be applied
 * @return string
 */
export declare function parse(dateTimeString: string, formatString: string, overrideZone?: TimeZone, allowTrailing?: boolean): AwareTimeStruct;
